mapper.select:
SELECT count(id) FROM city
province_id = #{provId}
and `name` LIKE CONCAT('%', #{key})

SELECT id as cityId, `name`, province_id as provinceId FROM city
province_id = #{provId}
and `name` LIKE CONCAT('%', #{key})

mapper.update:
UPDATE address
SET province_id = #{provinceId}, city_id = #{cityId}, full_address = #{address}, last_update_date = now()
WHERE id = #{id}
