mapper.select:
SELECT count(id) FROM school
`city_id` = #{cityId}
and `name` LIKE CONCAT('%', #{key})

SELECT id as schoolId, `name` as schoolName FROM school
`city_id` = #{cityId}
and `name` LIKE CONCAT('%', #{key})

SELECT count(id) FROM class
`school_id` = #{schoolId}
and (`grade` LIKE CONCAT('%', #{key}) or `class` LIKE CONCAT('%', #{key}))

SELECT id as classId, grade,class FROM class
`school_id` = #{schoolId}
and (`grade` LIKE CONCAT('%', #{key}) or `class` LIKE CONCAT('%', #{key}))

SELECT id
FROM student
WHERE NAME = #{name} AND class_id = #{classId}

mapper.update:
UPDATE student
SET name = #{name}, class_id = #{classId}, sex = #{male}, birth = #{birth}, ring_number = #{ringNumber},
ring_phone = #{ringPhone}, last_update_date = #{lastUpdateDate}
WHERE id = #{id}
